export const createUserDashboardConfig = catchAsync(async (req, res) => {
  const { body } = req;
  body.createdBy = req.user._id;
  body.updatedBy = req.user._id;
  const { user } = req;
  const moveFileObj = {
    ...(body.banner && { banner: body.banner }),
  };
  body._id = mongoose.Types.ObjectId();
  await moveFiles({ body, user, moveFileObj });
  const options = {};
  const userDashboardConfigResult = await userDashboardConfigService.createUserDashboardConfig(body, options);
  if (userDashboardConfigResult) {
    const uploadedFileUrls = [];
    uploadedFileUrls.push(userDashboardConfigResult.banner);
    await TempS3.updateMany({ url: { $in: uploadedFileUrls } }, { active: true });
  }
  return res.status(httpStatus.CREATED).send({ results: userDashboardConfigResult });
});

export const updateUserDashboardConfig = catchAsync(async (req, res) => {
  const { body } = req;
  body.updatedBy = req.user;
  const { userDashboardConfigId } = req.params;
  const { user } = req;
  const moveFileObj = {
    ...(body.banner && { banner: body.banner }),
  };
  body._id = userDashboardConfigId;
  await moveFiles({ body, user, moveFileObj });
  const filter = {
    _id: userDashboardConfigId,
  };
  const options = { new: true };
  const userDashboardConfigResult = await userDashboardConfigService.updateUserDashboardConfig(filter, body, options);
  // tempS3
  if (userDashboardConfigResult) {
    const uploadedFileUrls = [];
    uploadedFileUrls.push(userDashboardConfigResult.banner);
    await TempS3.updateMany({ url: { $in: uploadedFileUrls } }, { active: true });
  }
  return res.status(httpStatus.OK).send({ results: userDashboardConfigResult });
});

